YUI.add("anim-curve",function(u,e){u.Anim.behaviors.curve={set:function(e,n,r,i,t,c,o){r=r.slice.call(r),i=i.slice.call(i);t=o(t,0,100,c)/100;i.unshift(r),e._node.setXY(u.Anim.getBezier(i,t))},get:function(e){return e._node.getXY()}},u.Anim.getBezier=function(e,n){for(var r,i=e.length,t=[],c=0;c<i;++c)t[c]=[e[c][0],e[c][1]];for(r=1;r<i;++r)for(c=0;c<i-r;++c)t[c][0]=(1-n)*t[c][0]+n*t[parseInt(c+1,10)][0],t[c][1]=(1-n)*t[c][1]+n*t[parseInt(c+1,10)][1];return[t[0][0],t[0][1]]}},"@VERSION@",{requires:["anim-xy"]});